// Header.js

import React , {Component} from 'react';


class Header extends Component{

    constructor(props) {
        super(props);
        this.state = {text:  "English (US)" };

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e) {
        var selectedLanguage = e.target.attributes.getNamedItem('language').value;
        this.setState(state => ({
            text: selectedLanguage
        }));

    }



    render(){
        return (
            <header>

                <div className="container">
                    <div className="header-top">
                        <a className="navbar-brand" href="/">
                            <img src="img/icon-tailor-logo.svg" alt="Start My Icon" />
                        </a>
                        <div className="header-btn main_dropdown_header">
                            <div className="dropdown">
                                <button className=" btn language-btn-header dropdown-toggle" type="button" id="dropdownMenu1" data-toggle="dropdown" aria-haspopup="true" aria-expanded="true">
                                {this.state.text} <i className="material-icons">keyboard_arrow_down</i>
                                </button>
                                <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
                                    <li><a onClick={this.handleClick}  href="#" language="English (US)" data-value="action"><span><img src="img/us-flag.png" /></span> English (US)</a></li>
                                    <li><a onClick={this.handleClick}  href="#" language="French" data-value="another action"><span><img src="img/france.svg" /></span> French</a></li>
                                </ul>
                                <a href="#" className="link-head" data-toggle="modal" data-target="#signin">Login</a>
                            </div>
                        </div>
                        <div className="clearfix"></div>
                    </div>
                </div>
            </header>
        )
    }
}

export default Header;
